INSERT INTO public.official_monsters (name, challenge_level, hit_points, armor_class, speed, attacks, abilities, treasure, tags, source, author_notes, icon_url, art_url, strength_mod, dexterity_mod, constitution_mod, intelligence_mod, wisdom_mod, charisma_mod) VALUES ('The Tarrasque', 30, 140, 22, 'triple near (burrow, swim)', '[{"name":"thrash","type":"ranged","damage":"3d10","range":"near","description":"","attackBonus":13,"numberOfAttacks":4}]'::jsonb, '[{"name":"Legendary","description":"Only damaged by magical sources. Hostile spells targeting the tarrasque are DC 18 to cast."},{"name":"Deep Dweller","description":"Immune to harm from fire and cold. Amphibious."},{"name":"Permanent Death","description":"Cannot be permanently killed unless a wish spell is cast on it while it is at 0 HP."},{"name":"Rampage","description":"In place of attacks, move far in a straight line and make one bite attack. On a hit, triple damage."},{"name":"Reflective Carapace","description":"Immune to rays, blasts, or bolts of energy. 1:6 chance these are reflected back at their originator."},{"name":"Regeneration","description":"Regains 4d10 lost HP at the beginning of its turn."},{"name":"Sever","description":"On a natural attack roll of 18+, the attack also severs a random limb. 1d6: 1. Head, 2-4. Arm, 5-6. Leg."},{"name":"Swallow","description":"DC 18 STR or swallowed whole. Total darkness inside and 4d10 damage per round. Tarrasque regurgitates all swallowed if dealt at least 30 damage in one round to the inside of its gullet."}]'::jsonb, NULL, '{"type":["monstrosity"],"location":["cave","underground"]}'::jsonb, 'Shadowdark Core', 'A colossal, four-legged reptile with crocodilian jaws, amber scales, and a diamond-hard, spiked carapace. It towers overhead like a mountain, able to swallow entire villages in one gulp. The tarrasque hibernates deep in the earth or at the bottom of the sea for centuries, only awakening long enough to fill its vast belly in an indiscriminate rampage of terror and destruction. There is only one tarrasque, and it is the most dreaded creature to walk the earth.', NULL, NULL, 7, 2, 5, -3, 1, -3);
INSERT INTO public.official_monsters (name, challenge_level, hit_points, armor_class, speed, attacks, abilities, treasure, tags, source, author_notes, icon_url, art_url, strength_mod, dexterity_mod, constitution_mod, intelligence_mod, wisdom_mod, charisma_mod) VALUES ('The Ten-Eyed Oracle', 18, 85, 17, 'near (fly)', '[]'::jsonb, '[{"name":"Legendary","description":"Only damaged by magical sources. Hostile spells targeting The Ten-Eyed Oracle are DC 18 to cast."},{"name":"Eyestalk Ray","description":"Each ray can shoot once per round and target one creature or an object up to 1,000 pounds (up to four on same target).\n1. Charm. DC 15 CHA or become ally for 1d4 rounds.\n2. Hold. DC 15 STR or paralyzed 1d4 rounds.\n3. Sleep. DC 15 WIS or asleep 1d4 rounds.\n4. Polymorph. DC 15 INT or turned into vermin for 1d4 rounds.\n5. Cancel. DC 15 DEX or all magical effects on target end.\n6. Confusion. DC 15 WIS or attack a random ally within near.\n7. Telekinesis. DC 15 STR or hover target up to double near.\n8. Disintegrate. DC 15 DEX or 5d8 damage (object destroyed).\n9. Petrify. DC 15 CON or petrified.\n10. Death. DC 15 CON or drop to 0 HP with a death timer of 1."}]'::jsonb, NULL, '{"type":["monstrosity"],"location":["cave","underground"]}'::jsonb, 'Shadowdark Core', 'A floating mass of rubbery skin crusted with barnacles. Its ten eyestalks writhe like snakes, and a deep, circular scar mars its central body, blinding what was once a large eye above a lipless maw. The Ten-Eyed Oracle stalks the Shadowdark, burbling mad prophesies and somehow moving freely between the lightless fathoms of the earth. The keepers of the deep lore suspect The Ten-Eyed Oracle was once a benevolent ally of mankind, but an unknown calamity drove it to insanity and reckless hatred.', NULL, NULL, 4, 5, 4, 5, 4, 4);
INSERT INTO public.official_monsters (name, challenge_level, hit_points, armor_class, speed, attacks, abilities, treasure, tags, source, author_notes, icon_url, art_url, strength_mod, dexterity_mod, constitution_mod, intelligence_mod, wisdom_mod, charisma_mod) VALUES ('The Wandering Merchant', 15, 71, 16, 'near', '[{"name":"+3 vorpal bastard sword +9 (1d10 + lop)","type":"melee","damage":"special","range":"close","description":"","numberOfAttacks":4}]'::jsonb, '[{"name":"Legendary","description":"Only damaged by magical sources. Hostile spells targeting The Wandering Merchant are DC 18 to cast."},{"name":"Amulet of Rahm-Hotep","description":"In place of attacks, teleport to a random location in the multiverse."},{"name":"Bottomless Bag","description":"Contains 200 gear slots in magical sub-pockets. Weighs as much as a normal backpack. The Wandering Merchant can summon it to himself from anywhere by snapping his fingers."},{"name":"Dice of Truth","description":"A set of three six-sided dice whose rolls cannot be magically or mundanely altered in any way."},{"name":"Lop","description":"On a natural attack roll of 18-20, behead the target."},{"name":"Reckoning","description":"Any creature who steals from The Wandering Merchant develops a curse. Over the next 2d8 days, that creature turns into a cockatrice. This curse can only be lifted by a wish spell or by returning the stolen goods."},{"name":"Strange Lands","description":"The Wandering Merchant does not suffer any ill effects from the natural environment he does not wish to suffer."}]'::jsonb, NULL, '{"type":["humanoid"],"location":["water","coastal"]}'::jsonb, 'Shadowdark Core', 'A cheerful merchant who appears to be a hale, middle-aged human man with a handlebar mustache. He wears a white shirt, breeches, and leather apron, and he hauls a towering backpack bursting at the seams. Few know the merchant''s true nature and origins, though some theorize he is an immortal being or a god (they are not entirely incorrect). The Wandering Merchant always has something useful, far-flung, or incredible to sell at a reasonable price, and there''s no telling when or where he''ll show up next...', NULL, NULL, 3, 3, 4, 3, 4, 5);
