INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Acid Arrow', 'acid_arrow', 'You conjure a corrosive bolt that hits one foe, dealing 1d6 damage a round. The bolt remains in the target for as long as you focus.', '["wizard"]'::jsonb, 'Focus', 'Far', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Alarm', 'alarm', 'You touch one object, such as a door threshold, setting a magical alarm on it. If any creature you do not designate while casting the spell touches or crosses past the object, a magical bell sounds in your head.', '["wizard"]'::jsonb, '1 day', 'Close', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Alter Self', 'alter_self', 'You magically change your physical form, gaining one feature that modifies your existing anatomy. For example, you can grow functional gills on your neck or bear claws on your fingers. This spell can’t grow wings or limbs.', '["wizard"]'::jsonb, '5 rounds', 'Self', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Animate Dead', 'animate_dead', 'You touch one humanoid’s remains, and it rises as a zombie or skeleton under your control. The remains must have at least three limbs and its head intact. The undead creature acts on your turn. After 1 day, the creature collapses into grave dust.', '["wizard"]'::jsonb, '1 day', 'Close', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Antimagic Shell', 'antimagic_shell', 'An invisible, near-sized cube of null-magic appears centered on you. Within the cube, no spells can be cast. Magic items and spells have no effect in the zone, and no magic can enter. The cube moves with you. Spells such as dispel magic have no effect on it. Another antimagic shell does not affect this one.', '["wizard"]'::jsonb, 'Focus', 'Self', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Arcane Eye', 'arcane_eye', 'You conjure an invisible, grape-sized eye within range. You can see through the eye. It can see in the dark out to near range, fly near on your turn, and squeeze through openings as narrow as a keyhole.', '["wizard"]'::jsonb, 'Focus', 'Near', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Augury', 'augury', 'You interpret the meaning of supernatural portents and omens. Ask the GM one question about a specific course of action. The GM says whether the action will lead to “weal” or “woe.”', '["priest"]'::jsonb, 'Instant', 'Self', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Bless', 'bless', 'One creature you touch gains a luck token.', '["priest"]'::jsonb, 'Instant', 'Close', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Blind-Deafen', 'blind-deafen', 'You utter a divine censure, blinding or deafening one creature you can see in range. The creature has disadvantage on tasks requiring the lost sense.', '["priest"]'::jsonb, 'Focus', 'Near', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Burning Hands', 'burning_hands', 'You spread your fingers with thumbs touching, unleashing a circle of flame that roars out to a close area around where you stand. Creatures within the area of effect take 1d6 damage, and flammable objects catch fire.', '["wizard"]'::jsonb, 'Instant', 'Close', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Charm Person', 'charm_person', 'You magically beguile one humanoid of level 2 or less within near range, who regards you as a friend for the duration. The spell ends if you or your allies do anything to hurt it that it notices. The target knows you magically enchanted it after the spell ends.', '["wizard"]'::jsonb, '1d8 days', 'Near', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Cleansing Weapon', 'cleansing_weapon', 'One weapon you touch is wreathed in purifying flames. It deals an additional 1d4 damage (1d6 vs. undead) for the duration.', '["priest"]'::jsonb, '5 rounds', 'Close', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Cloudkill', 'cloudkill', 'A putrid cloud of yellow poison fills a near-sized cube within range. It spreads around corners. Creatures inside the cloud are blinded and take 2d6 damage at the beginning of their turns. A creature of LV 9 or less that ends its turn fully inside the cloud dies.', '["wizard"]'::jsonb, '5 rounds', 'Far', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Command', 'command', 'You issue a verbal command to one creature in range who can understand you. The command must be one word, such as “kneel.” The target obeys the command for as long as you focus. If your command is ever directly harmful to the creature, it may make a Charisma check vs. your last spellcasting check. On a success, the spell ends.', '["priest"]'::jsonb, 'Focus', 'Far', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Commune', 'commune', 'You seek your god''s counsel. Ask the GM up to three yes or no questions. The GM truthfully answers "yes" or "no" to each. If you cast this spell more than once in 24 hours, treat a failed spellcasting check for it as a critical failure instead.', '["priest"]'::jsonb, 'Instant', 'Self', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Confusion', 'confusion', 'You mesmerize one creature you can see in range. The target can''t take actions, and it moves in a random direction on its turn. If the target is LV 9+, it may make a WIS check vs. your last spellcasting check at the start of its turn to end the spell.', '["wizard"]'::jsonb, 'Focus', 'Near', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Control Water', 'control_water', 'You move and shape water. You can cause a section of water up to 100 feet in width and depth to change shape, defy gravity, or flow in a different direction.', '["priest","wizard"]'::jsonb, 'Focus', 'Far', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Create Undead', 'create_undead', 'You conjure a vengeful undead creature to do your bidding. When you cast this spell, you choose to summon either a wight or wraith. It appears next to you and is under your control. The undead creature acts on your turn. After 1 day, it melts away into smoke.', '["wizard"]'::jsonb, '1 day', 'Close', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Cure Wounds', 'cure_wounds', 'Your touch restores ebbing life. Roll a number of d6s equal to 1 + half your level (rounded down). One target you touch regains that many hit points.', '["priest"]'::jsonb, 'Instant', 'Close', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Detect Magic', 'detect_magic', 'You can sense the presence of magic within near range for the spell''s duration. If you focus for two rounds, you discern its general properties. Full barriers block this spell.', '["wizard"]'::jsonb, 'Focus', 'Near', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Detect Thoughts', 'detect_thoughts', 'You peer into the mind of one creature you can see within range. Each round, you learn the target’s immediate thoughts. On its turn, the target makes a Wisdom check vs. your last spellcasting check. If the target succeeds, it notices your presence in its mind and the spell ends.', '["wizard"]'::jsonb, 'Focus', 'Near', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Dimension Door', 'dimension_door', 'You teleport yourself and up to one other willing creature within close to any point you can see.', '["wizard"]'::jsonb, 'Instant', 'Self', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Disintegrate', 'disintegrate', 'A green ray shoots from your finger and turns a creature or object into ash. A target creature of LV 5 or less instantly dies. If it is LV 6+, it takes 3d8 damage instead. A non-magical object up to the size of a large tree is destroyed.', '["wizard"]'::jsonb, 'Instant', 'Far', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Dispel Magic', 'dispel_magic', 'End one spell that affects one target you can see in range.', '["wizard"]'::jsonb, 'Instant', 'Near', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Divination', 'divination', 'You throw the divining bones or peer into the blackness between the stars, seeking a portent. You can ask the GM one yes or no question. The GM truthfully answers "yes" or "no." If you cast this spell more than once in 24 hours, treat a failed spellcasting check for it as a critical failure instead.', '["wizard"]'::jsonb, 'Instant', 'Self', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Divine Vengeance', 'divine_vengeance', 'You become the divine avatar of your god''s wrath, wreathed in holy flames or a black aura of smoldering corruption. For the spell''s duration, you can fly a near distance, your weapons are magical, and you have a +4 bonus to your weapon attacks and damage.', '["priest"]'::jsonb, '10 rounds', 'Self', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Dominion', 'dominion', 'Mighty beings come to your aid. The beings must have a combined total of 16 levels or less. Chaotic PCs summon demons/devils, and lawful or neutral PCs summon angels. The beings act of free will to aid you on your turn. After 10 rounds, they return to their realms. You cannot cast this spell again until you complete penance.', '["priest"]'::jsonb, '10 rounds', 'Near', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Fabricate', 'fabricate', 'This spell can''t target creatures. You turn a tree-sized collection of raw materials into a finished work. For example, you convert a pile of bricks or rocks into a bridge. The finished work converts back to raw materials when the spell ends.', '["wizard"]'::jsonb, '10 rounds', 'Near', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Feather Fall', 'feather_fall', 'You may make an attempt to cast this spell when you fall. Your rate of descent slows so that you land safely on your feet.', '["wizard"]'::jsonb, 'Instant', 'Self', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Fireball', 'fireball', 'You hurl a small flame that erupts into a fiery blast. All creatures in a near-sized cube around where the flame lands take 4d6 damage.', '["wizard"]'::jsonb, 'Instant', 'Far', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Fixed Object', 'fixed_object', 'An object you touch that weighs no more than 5 pounds becomes fixed in its current location. It can support up to 5,000 pounds of weight for the duration of the spell.', '["wizard"]'::jsonb, '5 rounds', 'Close', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Flame Strike', 'flame_strike', 'You call down a holy pillar of fire, immolating one creature you can see within range. The target takes 2d6 damage.', '["priest"]'::jsonb, 'Instant', 'Far', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Floating Disk', 'floating_disk', 'You create a floating, circular disk of force with a concave center. It can carry up to 20 gear slots. It hovers at waist level and automatically stays within near of you. It can’t cross over drop- offs or pits taller than a human.', '["wizard"]'::jsonb, '10 rounds', 'Near', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Fly', 'fly', 'Your feet lift from the ground, and you take to the air like a hummingbird. You can fly near for the spell''s duration and are able to hover in place.', '["wizard"]'::jsonb, '5 rounds', 'Self', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Gaseous Form', 'gaseous_form', 'You and your gear turn into a cloud of smoke for the spell''s duration. You can fly and pass through any gap that smoke could. You can sense the terrain and any movement around you out to a near distance. You can''t cast spells while in this form.', '["wizard"]'::jsonb, '10 rounds', 'Self', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Heal', 'heal', 'One creature you touch is healed to full hit points. You cannot cast this spell again until you complete a rest.', '["priest"]'::jsonb, 'Instant', 'Close', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Hold Monster', 'hold_monster', 'You paralyze one creature you can see within range. If the target is LV 9+, it may make a STR check vs. your last spellcasting check at the start of its turn to end the spell.', '["wizard"]'::jsonb, 'Focus', 'Near', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Hold Person', 'hold_person', 'You magically paralyze one humanoid creature of LV 4 or less you can see within range.', '["wizard"]'::jsonb, 'Focus', 'Near', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Hold Portal', 'hold_portal', 'You magically hold a portal closed for the duration. A creature must make a successful STR check vs. your spellcasting check to open the portal. The knock spell ends this spell.', '["wizard"]'::jsonb, '10 rounds', 'Near', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Holy Weapon', 'holy_weapon', 'One weapon you touch is imbued with a sacred blessing. The weapon becomes magical and has +1 to attack and damage rolls for the duration.', '["priest"]'::jsonb, '5 rounds', 'Close', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Illusion', 'illusion', 'You create a convincing visible and audible illusion that fills up to a near-sized cube in range. The illusion cannot cause harm, but creatures who believe the illusion is real react to it as though it were. A creature who inspects the illusion from afar must pass a Wisdom check vs. your last spellcasting check to perceive the false nature of the illusion. Touching the illusion also reveals its false nature.', '["wizard"]'::jsonb, 'Focus', 'Far', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Invisibility', 'invisibility', 'A creature you touch becomes invisible for the spell’s duration. The spell ends if the target attacks or casts a spell.', '["wizard"]'::jsonb, '10 rounds', 'Close', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Judgment', 'judgment', 'You instantly banish a creature you touch, sending it and all possessions it carries to face the judgment of your god. You can banish an intelligent creature of LV 10 or less. When the creature returns in 5 rounds, it has been healed to full hit points if its deeds pleased your god. It has been reduced to 1 hit point if its deeds angered your god. If your god can''t judge its actions, it is unchanged.', '["priest"]'::jsonb, '5 rounds', 'Close', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Knock', 'knock', 'A door, window, gate, chest, or portal you can see within range instantly opens, defeating all mundane locks and barriers. This spell creates a loud knock audible to all within earshot.', '["wizard"]'::jsonb, 'Instant', 'Near', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Lay To Rest', 'lay_to_rest', 'You instantly send an undead creature you touch to its final afterlife, destroying it utterly. You can target an undead creature of LV 9 or less.', '["priest"]'::jsonb, 'Instant', 'Close', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Levitate', 'levitate', 'You can float a near distance vertically per round on your turn. You can also push against solid objects to move horizontally.', '["wizard"]'::jsonb, 'Focus', 'Self', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Light', 'light', 'One object you touch glows with bright, heatless light, illuminating out to a near distance for 1 hour of real time.', '["priest","wizard"]'::jsonb, '1 hour real time', 'Close', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Lightning Bolt', 'lightning_bolt', 'You shoot a blue-white ray of lightning from your hands, hitting all creatures in a straight line out to a far distance. Creatures struck by the lightning take 3d6 damage.', '["wizard"]'::jsonb, 'Instant', 'Far', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Mage Armor', 'mage_armor', 'An invisible layer of magical force protects your vitals. Your armor class becomes 14 (18 on a critical spellcasting check) for the spell’s duration.', '["wizard"]'::jsonb, '10 rounds', 'Self', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Magic Circle', 'magic_circle', 'You conjure a circle of runes out to near-sized cube centered on yourself and name a type of creature (for example, demons). For the spell’s duration, creatures of the chosen type cannot attack or cast a hostile spell on anyone inside the circle. The chosen creatures also can’t possess, compel, or beguile anyone inside the circle.', '["wizard"]'::jsonb, 'Focus', 'Near', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Magic Missile', 'magic_missile', 'You have advantage on your check to cast this spell. A glowing bolt of force streaks from your open hand, dealing 1d4 damage to one target.', '["wizard"]'::jsonb, 'Instant', 'Far', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Mass Cure', 'mass_cure', 'All allies within near range of you regain 2d6 hit points.', '["priest"]'::jsonb, 'Instant', 'Near', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Mirror Image', 'mirror_image', 'You create a number of illusory duplicates of yourself equal to half your level rounded down (minimum 1). The duplicates surround you and mimic you. Each time a creature attacks you, the attack misses and causes one of the duplicates to evaporate. If all of the illusions have disappeared, the spell ends.', '["wizard"]'::jsonb, '5 rounds', 'Self', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Misty Step', 'misty_step', 'In a puff of smoke, you teleport a near distance to an area you can see.', '["wizard"]'::jsonb, 'Instant', 'Self', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Passwall', 'passwall', 'A tunnel of your height opens in a barrier you touch and lasts for the duration. The passage can be up to near distance in length and must be in a straight line.', '["wizard"]'::jsonb, '5 rounds', 'Close', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Pillar of Salt', 'pillar_of_salt', 'A creature you target turns into a statue made of hardened salt. You can target a creature you can see of LV 5 or less. If you successfully focus on this spell for 3 rounds in a row, the transformation becomes permanent.', '["priest"]'::jsonb, 'Focus', 'Near', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Plane Shift', 'plane_shift', 'You fold space and time, transporting yourself and all willing creatures within close range to a location on another plane of your choice. Unless you have been to your intended location before, you appear in a random place on the destination plane.', '["priest","wizard"]'::jsonb, 'Instant', 'Close', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Polymorph', 'polymorph', 'You transform a creature you touch into another natural creature you choose of equal or smaller size. Any gear the target carries melds into its new form. The target gains the creature''s physical stats and features, but it retains its non-physical stats and features. If the target goes to 0 hit points, it reverts to its true form at half its prior hit points. You can target any willing creature with this spell, or an unwilling creature whose level is less than or equal to half your level rounded down (min. 1).', '["wizard"]'::jsonb, '10 rounds', 'Close', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Power Word Kill', 'power_word_kill', 'You utter the Word of Doom. One creature you target of LV 9 or less dies if it hears you. Treat a failed spellcasting check for this spell as a critical failure, and roll the mishap with disadvantage.', '["wizard"]'::jsonb, 'Instant', 'Near', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Prismatic Orb', 'prismatic_orb', 'You send a strobing orb of energy streaking toward a target within range. Choose an energy type from fire, cold, or electricity. The orb deals 3d8 damage and delivers a concussive blast of the chosen energy type. If the energy type is anathema to the target''s existence (for example, cold energy against a fire elemental), the orb deals double damage to it instead.', '["wizard"]'::jsonb, 'Instant', 'Far', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Prophecy', 'prophecy', 'You commune directly with your god for guidance. Ask the GM one question. The GM answers the question truthfully using the knowledge your god possesses. Deities are mighty, but not omniscient. You cannot cast this spell again until you complete penance.', '["priest"]'::jsonb, 'Instant', 'Self', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Protection From Energy', 'protection_from_energy', 'One creature you touch becomes impervious to the wild fury of the elements. Choose fire, cold, or electricity. For the spell''s duration, the target is immune to harm from energy of the chosen type.', '["wizard"]'::jsonb, 'Focus', 'Close', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Protection From Evil', 'protection_from_evil', 'For the spell’s duration, chaotic beings have disadvantage on attack rolls and hostile spellcasting checks against the target. These beings also can’t possess, compel, or beguile it. When cast on an already- possessed target, the possessing entity makes a CHA check vs. the last spellcasting check. On a failure, the entity is expelled.', '["priest","wizard"]'::jsonb, 'Focus', 'Close', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Rebuke Unholy', 'rebuke_unholy', 'You rebuke creatures who oppose your alignment, forcing them to flee. You must present a holy symbol to cast this spell. If you are lawful or neutral, this spell affects demons, devils, and outsiders. If you are chaotic, this spell affects angels and natural creatures of the wild. Affected creatures within near of you must make a CHA check vs. your spellcasting check. If a creature fails by 10+ points and is equal to or less than your level, it is destroyed. Otherwise, on a fail, it flees from you for 5 rounds.', '["priest"]'::jsonb, 'Instant', 'Near', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Regenerate', 'regenerate', 'A creature you touch regains 1d4 hit points on your turn for the duration. This spell also regrows lost body parts.', '["priest"]'::jsonb, 'Focus', 'Close', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Resilient Sphere', 'resilient_sphere', 'You conjure a weightless, glassy sphere around you that extends out to close range. For the spell''s duration, nothing can pass through or crush the sphere. You can roll the sphere a near distance on your turn.', '["wizard"]'::jsonb, '5 rounds', 'Close', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Restoration', 'restoration', 'With the touch of your hands, you expunge curses and illnesses. One curse, illness, or affliction of your choice affecting the target creature ends.', '["priest"]'::jsonb, 'Instant', 'Close', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Scrying', 'scrying', 'You look into a crystal ball or reflecting pool, calling up images of a distant place. For the spell''s duration, you can see and hear a creature or location you choose that is on the same plane. This spell is DC 18 to cast if you try to scry on a creature or location that is unfamiliar to you. Each round, creatures you view may make a Wisdom check vs. your last spellcasting check. On a success, they become aware of your magical observation.', '["wizard"]'::jsonb, 'Focus', 'Self', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Sending', 'sending', 'You send a brief, mental message to any creature with whom you are familiar who is on the same plane.', '["wizard"]'::jsonb, 'Instant', 'Unlimited', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Shapechange', 'shapechange', 'You transform yourself and any gear you carry into another natural creature you''ve seen of level 10 or less. You assume the creature''s physical stats and features, but you retain your non-physical stats and features (including INT, WIS, and CHA). If you go to 0 HP while under the effects of this spell, you revert to your true form at 1 HP.', '["wizard"]'::jsonb, 'Focus', 'Self', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Shield of Faith', 'shield_of_faith', 'A protective force wrought of your holy conviction surrounds you. You gain a +2 bonus to your armor class for the duration.', '["priest"]'::jsonb, '5 rounds', 'Self', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Silence', 'silence', 'You magically mute sound in a near cube within the spell’s range. Creatures inside the area are deafened, and any sounds they create cannot be heard.', '["wizard"]'::jsonb, 'Focus', 'Far', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Sleep', 'sleep', 'You weave a lulling spell that fills a near-sized cube extending from you. Living creatures in the area of effect fall into a deep sleep if they are LV 2 or less. Vigorous shaking or being injured wakes them.', '["wizard"]'::jsonb, 'Instant', 'Near', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Smite', 'smite', 'You call down punishing flames on a creature you can see within range. It takes 1d6 damage.', '["priest"]'::jsonb, 'Instant', 'Near', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Speak With Dead', 'speak_with_dead', 'A dead body you touch answers your questions in a distant, wheezing voice. You can ask the dead body up to three yes or no questions (one at a time). The GM truthfully answers "yes" or "no" to each. If you cast this spell more than once in 24 hours, treat a failed spellcasting check for it as a critical failure instead.', '["priest","wizard"]'::jsonb, 'Instant', 'Close', 3, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Stoneskin', 'stoneskin', 'Your skin becomes like granite. For the spell''s duration, your armor class becomes 17 (20 on a critical spellcasting check).', '["wizard"]'::jsonb, '10 rounds', 'Self', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Summon Extraplanar', 'summon_extraplanar', 'You reach into the outer planes, summoning forth a creature. You summon an elemental or outsider of LV 7 or less. The creature is under your control and acts on your turn. If you lose focus on this spell, you lose control of the creature and it becomes hostile toward you and your allies. You must pass a spellcasting check on your turn to return the creature to the outer planes.', '["wizard"]'::jsonb, 'Focus', 'Near', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Telekinesis', 'telekinesis', 'You lift a creature or object with your mind. Choose a target that weighs 1,000 pounds or less. You can move it a near distance in any direction and hold it in place.', '["wizard"]'::jsonb, 'Focus', 'Far', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Teleport', 'teleport', 'You and any willing creatures you choose within close range teleport to a location you specify on your same plane. You can travel to a known teleportation sigil or to a location you''ve been before. Otherwise, you have a 50% chance of arriving off-target.', '["wizard"]'::jsonb, 'Instant', 'Close', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Turn Undead', 'turn_undead', 'You rebuke undead creatures, forcing them to flee. You must present a holy symbol to cast this spell. Undead creatures within near of you must make a CHA check vs. your spellcasting check. If a creature fails by 10+ points and is equal to or less than your level, it is destroyed. Otherwise, on a fail, it flees from you for 5 rounds.', '["priest"]'::jsonb, 'Instant', 'Near', 1, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Wall of Force', 'wall_of_force', 'You lift your hands, conjuring a transparent wall of force. The thin wall must be contiguous and can cover a near-sized area in width and length. You choose its shape. Nothing on the same plane can physically pass through the wall.', '["wizard"]'::jsonb, '5 rounds', 'Near', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Web', 'web', 'You create a near-sized cube of sticky, dense spider web within the spell’s range. A creature stuck in the web can’t move and must succeed on a Strength check vs. your spellcasting check to free itself.', '["wizard"]'::jsonb, '5 rounds', 'Far', 2, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Wish', 'wish', 'This mighty spell alters reality. Make a single wish, stating it as exactly as possible. Your wish occurs, as interpreted by the GM. Treat a failed spellcasting check for this spell as a critical failure, and roll the mishap with disadvantage.', '["wizard"]'::jsonb, 'Instant', 'Self', 5, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Wrath', 'wrath', 'Your weapons become magical +2 and deal an additional d8 damage for the spell''s duration.', '["priest"]'::jsonb, '10 rounds', 'Self', 4, 'Shadowdark Core', NULL, NULL, NULL);
INSERT INTO public.official_spells (name, slug, description, classes, duration, range, tier, source, author_notes, icon_url, art_url) VALUES ('Zone of Truth', 'zone_of_truth', 'You compel a creature you can see to speak truth. It can’t utter a deliberate lie while within range.', '["priest"]'::jsonb, 'Focus', 'Near', 2, 'Shadowdark Core', NULL, NULL, NULL);
